fit_wbc_trans <- glm(BC ~ t_WBC_noNEU,
                     data = model_df_complete,
                     family = binomial) 

bind_rows(
  tidy(fit_wbc_orig),
  tidy(fit_wbc_trans)
  ) %>%
  filter(str_detect(term, 'WBC')) %>%
  select(term, estimate) %>%
  gt %>%
  fmt_number(estimate, decimals = 2)
